import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';


const TodoApp = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('/api/tasks');
      setTasks(response.data.reverse());
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const addTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      await axios.post('/api/tasks', { description: newTask });
      setNewTask('');
      fetchTasks();
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const toggleCompletion = async (taskId, completed) => {
    try {
      await axios.patch(`/api/tasks/${taskId}`, { completed: !completed });
      fetchTasks();
    } catch (error) {
      console.error('Error updating task completion:', error);
    }
  };

  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`/api/tasks/${taskId}`);
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const startEditing = (task) => {
    setEditingTask({ ...task });
  };

  const updateTask = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`/api/tasks/${editingTask._id}`, { description: editingTask.description });
      setEditingTask(null);
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  return (
    <div className="bg-gray-800 p-4 flex items-center justify-between">
    <div className="container mx-auto p-4 max-w-2xl">
      <h1 className="text-2xl font-bold mb-4">Todo App</h1>
      <form onSubmit={addTask} className="mb-4">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
          className="p-2 border rounded w-full"
        />
        <button type="submit" className="mt-2 button-submit text-white p-2 rounded">Add Task</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <li key={task._id} className="mb-2 p-2 border rounded">
            {editingTask && editingTask._id === task._id ? (
              <form onSubmit={updateTask}>
                <input
                  type="text"
                  value={editingTask.description}
                  onChange={(e) => setEditingTask({ ...editingTask, description: e.target.value })}
                  className="p-1 border rounded w-full"
                />
                <button type="submit" className="mt-1 bg-green-500 text-white p-1 rounded">Save</button>
                <button onClick={() => setEditingTask(null)} className="mt-1 ml-1 bg-gray-500 text-white p-1 rounded">Cancel</button>
              </form>
            ) : (
              <>
                <span className={task.completed ? 'line-through' : ''}>{task.description}</span>
                <button onClick={() => toggleCompletion(task._id, task.completed)} className="ml-2 bg-yellow-500 text-white p-1 rounded">
                  {task.completed ? 'Undo' : 'Complete'}
                </button>
                <button onClick={() => startEditing(task)} className="ml-2 bg-blue-500 text-white p-1 rounded">Edit</button>
                <button onClick={() => deleteTask(task._id)} className="ml-2 bg-red-500 text-white p-1 rounded">Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
    </div>
  );
};

export default TodoApp;
