import React, { useState, useEffect } from 'react';
import axios from 'axios';  // Don't forget to import axios
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('/api/tasks');
        setTasks(response.data.reverse());
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const addTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      await axios.post('/api/tasks', { description: newTask });
      setNewTask('');
      fetchTasks(); // Refresh task list after adding a new one
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };


  const toggleComplete = (id) => {
    // Toggle complete logic
  };

  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`/api/tasks/${taskId}`);
      fetchTasks(); // Refresh task list after deleting
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };


  const editTask = (id, newDescription) => {
    // Edit task logic
  };

  return (
    <div className="container">
      <h1>Todo App</h1>
      <form onSubmit={addTask}>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
        />
        <button type="submit">Add Task</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <button onClick={() => toggleComplete(task.id)}>
              {task.completed ? '✓' : '○'}
            </button>
            <span className={task.completed ? 'completed' : ''}>{task.description}</span>
            <div className="actions">
              <button onClick={() => editTask(task.id, task.description)}>Edit</button>
              <button onClick={() => deleteTask(task.id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
