import React, { useState, useEffect } from 'react';
import axios from 'axios';  // Don't forget to import axios
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  // Define the fetchTasks function outside of useEffect so it can be reused
  const fetchTasks = async () => {
    try {
      const response = await axios.get('/api/tasks');
      setTasks(response.data.reverse());
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Fetch tasks when the component mounts
  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;
    try {
      await axios.post('/api/tasks', { description: newTask });
      setNewTask('');  // Clear the input after adding a new task
      fetchTasks();  // Refresh the task list after adding a new one
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const toggleComplete = async (id, completed) => {
    try {
      await axios.patch(`/api/tasks/${id}`, { completed: !completed });
      fetchTasks();  // Refresh the task list after toggling completion
    } catch (error) {
      console.error('Error toggling task completion:', error);
    }
  };

  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`/api/tasks/${taskId}`);
      fetchTasks();  // Refresh the task list after deleting
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const editTask = async (id, newDescription) => {
    try {
      await axios.patch(`/api/tasks/${id}`, { description: newDescription });
      fetchTasks();  // Refresh the task list after editing
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  return (
    <div className="container">
      <h1>Todo App</h1>
      <form onSubmit={addTask}>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
        />
        <button type="submit">Add Task</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <li key={task._id}> {/* Make sure to use _id as your key if your task objects have _id */}
            <button onClick={() => toggleComplete(task._id, task.completed)}>
              {task.completed ? '✓' : '○'}
            </button>
            <span className={task.completed ? 'completed' : ''}>{task.description}</span>
            <div className="actions">
              <button onClick={() => editTask(task._id, prompt('Edit task:', task.description))}>Edit</button>
              <button onClick={() => deleteTask(task._id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;

